pragma solidity ^0.8.17;

import "../libraries/base64.sol";
import "../interfaces/INftFactory.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

contract NftFactory is INftFactory {
    struct Color {
        string value;
        string name;
    }
    struct Trait {
        string content;
        string name;
        Color color;
    }
    using Strings for uint256;

    string private constant SVG_END_TAG = "</svg>";

    function tokenURI(
        uint256 tokenId,
        uint256 seed
    ) external pure override returns (string memory) {
        uint256[4] memory colors = [
            (seed % 100000000000000) / 1000000000000,
            (seed % 10000000000) / 100000000,
            (seed % 1000000) / 10000,
            seed % 100
        ];
        Trait memory head = getHead(seed / 100000000000000, colors[0]);
        Trait memory face = getFace(
            (seed % 1000000000000) / 10000000000,
            colors[1]
        );
        Trait memory body = getBody((seed % 100000000) / 1000000, colors[2]);
        Trait memory foot = getFoot((seed % 10000) / 100, colors[3]);

        string memory rawSvg = string(
            abi.encodePacked(
                '<svg width="320" height="320" viewBox="0 0 320 320" xmlns="http://www.w3.org/2000/svg">',
                '<rect width="100%" height="100%" fill="#FF0000"/>',
                '<text x="170" y="130" font-family="Courier,monospace" font-weight="700" font-size="20" text-anchor="middle" letter-spacing="1">',
                '<animate attributeName="dy" values="0;50;0" dur="3s" repeatCount="indefinite" />',
                head.content,
                face.content,
                body.content,
                foot.content,
                "</text>",
                SVG_END_TAG
            )
        );

        string memory encodedSvg = Base64.encode(bytes(rawSvg));
        string memory description = "Flappy Owl Testnet, this nft is utility ecosystem for mining FRC coin.";

        return
            string(
                abi.encodePacked(
                    "data:application/json;base64,",
                    Base64.encode(
                        bytes(
                            abi.encodePacked(
                                "{",
                                '"name":"Flappy Owl-Testnet #',
                                tokenId.toString(),
                                '",',
                                '"description":"',
                                description,
                                '",',
                                '"image": "',
                                "data:image/svg+xml;base64,",
                                encodedSvg,
                                '",',
                                '"attributes": [{"trait_type": "Head", "value": "',
                                head.name,
                                " (",
                                head.color.name,
                                ")",
                                '"},',
                                '{"trait_type": "Face", "value": "',
                                face.name,
                                " (",
                                face.color.name,
                                ")",
                                '"},',
                                '{"trait_type": "Body", "value": "',
                                body.name,
                                " (",
                                body.color.name,
                                ")",
                                '"},',
                                '{"trait_type": "Foot", "value": "',
                                foot.name,
                                " (",
                                foot.color.name,
                                ")",
                                '"}',
                                "]",
                                "}"
                            )
                        )
                    )
                )
            );
    }

    function getColor(uint256 seed) private pure returns (Color memory) {
        if (seed == 10) {
            return Color("#000000", "Black");
        }
        if (seed == 11) {
            return Color("#00BFFF", "Deep Sky");
        }
        if (seed == 12) {
            return Color("#4169E1", "Royal Blue");
        }
        if (seed == 13) {
            return Color("#00ffff", "Aqua");
        }
        if (seed == 14) {
            return Color("#0bb4ff", "Blue Bolt");
        }
        if (seed == 15) {
            return Color("#1853ff", "Blue RYB");
        }
        if (seed == 16) {
            return Color("#35d435", "Hacker Green");
        }
        if (seed == 17) {
            return Color("#61ff75", "Screamin Green");
        }
        if (seed == 18) {
            return Color("#00bfa0", "Caribbean Green");
        }
        if (seed == 19) {
            return Color("#ffa300", "Orange");
        }
        if (seed == 20) {
            return Color("#FA8072", "Salmon");
        }
        if (seed == 21) {
            return Color("#d0f400", "Lime");
        }
        if (seed == 22) {
            return Color("#9b19f5", "Purple X11");
        }
        if (seed == 23) {
            return Color("#ffffff", "White");
        }
        if (seed == 24) {
            return Color("#f46a9b", "Cyclamen");
        }
        if (seed == 25) {
            return Color("#9400D3", "Dark Violet");
        }
        if (seed == 26) {
            return Color("#FFE4B5", "Moccasin");
        }
        if (seed == 27) {
            return Color("#FCE74C", "Pastel");
        }
        if (seed == 28) {
            return Color("#eeeeee", "Bright Gray");
        }
        if (seed == 29) {
            return Color("#708090", "Salte gray");
        }
        if (seed == 30) {
            return Color("#7FFF00", "Chartreuse");
        }
        if (seed == 31) {
            return Color("#ADFF2F", "Green Yellow");
        }
        if (seed == 32) {
            return Color("#DDA0DD", "Plum");
        }
        if (seed == 33) {
            return Color("#87CEFA", "Lightsky Blue");
        }
        if (seed == 34) {
            return Color("#98FB98", "Pale Green");
        }
        if (seed == 35) {
            return Color("#7FFFD4", "Aquamarine");
        }
        if (seed == 36) {
            return Color("#FFD700", "Gold");
        }
        if (seed == 37) {
            return Color("#000234", "Navy Deep");
        }
        if (seed == 38) {
            return Color("#FF69B4", "Hot Pink");
        }
        if (seed == 39) {
            return Color("#5070ff", "Crimson");
        }
        if (seed == 40) {
            return Color("#fffeee", "Cream");
        }

        return Color("", "");
    }

    function getHead(
        uint256 seed,
        uint256 colorSeed
    ) private pure returns (Trait memory) {
        Color memory color = getColor(colorSeed);
        string memory content;
        string memory name;
        if (seed == 10) {
            content = "'--'";
            name = "Flat";
        }
        if (seed == 11) {
            content = "((()))";
            name = "Two Block";
        }
        if (seed == 12) {
            content = "=*==*=";
            name = "Pigtails";
        }
        if (seed == 13) {
            content = "///";
            name = "Punk";
        }
        if (seed == 14) {
            content = "(\\_/)";
            name = "Feathers";
        }
        if (seed == 15) {
            content = "/--/";
            name = "Horns";
        }
        if (seed == 16) {
            content = "~~~";
            name = "Curly hair";
        }
        if (seed == 17) {
            content = "\\\\\\";
            name = "Reverse Punk";
        }

        return
            Trait(
                string(
                    abi.encodePacked(
                        '<tspan fill="',
                        color.value,
                        '">',
                        content,
                        "</tspan>"
                    )
                ),
                name,
                color
            );
    }

    function getFace(
        uint256 seed,
        uint256 colorSeed
    ) private pure returns (Trait memory) {
        Color memory color = getColor(colorSeed);
        string memory content;
        string memory name;
        if (seed == 10) {
            content = "(o,o)";
            name = "Eyes open";
        }
        if (seed == 11) {
            content = "(-,-)";
            name = "Sleeping";
        }
        if (seed == 12) {
            content = "(o,-)";
            name = "Wink";
        }
        if (seed == 13) {
            content = "(o,O)";
            name = "Suspicious";
        }
        if (seed == 14) {
            content = "(^,^)";
            name = "Smile";
        }
        if (seed == 15) {
            content = "(o-o)";
            name = "Glasses";
        }
        if (seed == 16) {
            content = "[=,=]";
            name = "Robot";
        }
        if (seed == 16) {
            content = "($,$)";
            name = "When Lambo";
        }

        return
            Trait(
                string(
                    abi.encodePacked(
                        '<tspan dy="20" x="170" fill="',
                        color.value,
                        '">',
                        content,
                        "</tspan>"
                    )
                ),
                name,
                color
            );
    }

    function getBody(
        uint256 seed,
        uint256 colorSeed
    ) private pure returns (Trait memory) {
        Color memory color = getColor(colorSeed);
        string memory content;
        string memory name;
        if (seed == 10) {
            content = "///[ :-]\\\\\\";
            name = "Feathers";
        }
        if (seed == 11) {
            content = "///{\\S/}\\\\\\";
            name = "Swag";
        }
        if (seed == 12) {
            content = "///{=|=}\\\\\\";
            name = "Muscular";
        }
        if (seed == 13) {
            content = "///(\\+/)\\\\\\";
            name = "Priest";
        }
        if (seed == 14) {
            content = "///{ :~}\\\\\\";
            name = "Shirt";
        }
        if (seed == 15) {
            content = "///{\\:/}\\\\\\";
            name = "Suit";
        }
        if (seed == 16) {
            content = "///{\\*/}\\\\\\";
            name = "Medals";
        }

        return
            Trait(
                string(
                    abi.encodePacked(
                        '<tspan dy="25" x="170" fill="',
                        color.value,
                        '">',
                        content,
                        "</tspan>"
                    )
                ),
                name,
                color
            );
    }

    function getFoot(
        uint256 seed,
        uint256 colorSeed
    ) private pure returns (Trait memory) {
        Color memory color = getColor(colorSeed);
        string memory content;
        string memory name;
        if (seed == 10) {
            content = '~"~"~';
            name = "Holding food";
        }
        if (seed == 11) {
            content = '" "';
            name = "Normal";
        }

        return
            Trait(
                string(
                    abi.encodePacked(
                        '<tspan dy="25" x="170" fill="',
                        color.value,
                        '">',
                        content,
                        "</tspan>"
                    )
                ),
                name,
                color
            );
    }

    function calculateColorCount(
        uint256[4] memory colors
    ) private pure returns (string memory) {
        uint256 count;
        for (uint256 i = 0; i < 4; i++) {
            for (uint256 j = 0; j < 4; j++) {
                if (colors[i] == colors[j]) {
                    count++;
                }
            }
        }

        if (count == 4) {
            return "4";
        }
        if (count == 6) {
            return "3";
        }
        if (count == 8 || count == 10) {
            return "2";
        }
        if (count == 16) {
            return "1";
        }

        return "0";
    }
}

